import { useState, useContext } from 'react';
import { 
  Box, 
  Button, 
  FormControl, 
  InputLabel, 
  MenuItem, 
  Select, 
  Typography 
} from '@mui/material';
import { CloudUpload } from '@mui/icons-material';
import { UserContext } from '../context/UserContext';
import { convertFile } from '../services/api';
import { validateFile } from '../utils/fileValidation';
import ConversionProgress from './ConversionProgress';
import Notification from './Notification';

const ALLOWED_FORMATS = {
  audio: ['mp3', 'wav', 'ogg', 'm4a'],
  video: ['mp4', 'avi', 'mov', 'wmv'],
  image: ['jpg', 'jpeg', 'png', 'gif', 'webp'],
  text: ['txt', 'doc', 'docx', 'pdf', 'rtf']
};

function FileConverter() {
  const [fileType, setFileType] = useState('');
  const [targetFormat, setTargetFormat] = useState('');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [notification, setNotification] = useState({
    open: false,
    message: '',
    severity: 'info'
  });

  const { guestId } = useContext(UserContext);

  const handleFileUpload = (event) => {
    try {
      const uploadedFile = event.target.files[0];
      validateFile(uploadedFile, fileType);
      setFile(uploadedFile);
    } catch (error) {
      showNotification(error.message, 'error');
    }
  };

  const handleConvert = async () => {
    try {
      setLoading(true);
      setProgress(0);

      const formData = new FormData();
      formData.append('file', file);
      formData.append('sourceType', fileType);
      formData.append('targetFormat', targetFormat);

      await convertFile(formData, (progressEvent) => {
        const percentCompleted = Math.round(
          (progressEvent.loaded * 100) / progressEvent.total
        );
        setProgress(percentCompleted);
      });

      showNotification('Conversion completed successfully!', 'success');
      
      // Reset form
      setFile(null);
      setTargetFormat('');
      
    } catch (error) {
      console.error('Conversion error:', error);
      showNotification(
        `Conversion failed: ${error.message || 'Unknown error'}`, 
        'error'
      );
    } finally {
      setLoading(false);
    }
  };

  const showNotification = (message, severity) => {
    setNotification({
      open: true,
      message,
      severity
    });
  };

  const closeNotification = () => {
    setNotification(prev => ({ ...prev, open: false }));
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h5" gutterBottom>
        Convert Your Files
      </Typography>
      
      <FormControl fullWidth sx={{ mb: 2 }}>
        <InputLabel>File Type</InputLabel>
        <Select
          value={fileType}
          onChange={(e) => setFileType(e.target.value)}
          disabled={loading}
        >
          <MenuItem value="audio">Audio</MenuItem>
          <MenuItem value="video">Video</MenuItem>
          <MenuItem value="image">Image</MenuItem>
          <MenuItem value="text">Text</MenuItem>
        </Select>
      </FormControl>

      {fileType && (
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Target Format</InputLabel>
          <Select
            value={targetFormat}
            onChange={(e) => setTargetFormat(e.target.value)}
            disabled={loading}
          >
            {ALLOWED_FORMATS[fileType]?.map((format) => (
              <MenuItem key={format} value={format}>
                {format.toUpperCase()}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      )}

      <Button
        variant="contained"
        component="label"
        startIcon={<CloudUpload />}
        disabled={!fileType || loading}
        fullWidth
        sx={{ mb: 2 }}
      >
        Upload File
        <input
          type="file"
          hidden
          onChange={handleFileUpload}
          accept={ALLOWED_FORMATS[fileType]?.map(format => `.${format}`).join(',')}
        />
      </Button>

      {file && (
        <Typography variant="body2" sx={{ mb: 2 }}>
          Selected file: {file.name}
        </Typography>
      )}

      {loading && (
        <ConversionProgress 
          progress={progress}
          status={`Converting... ${progress}%`}
        />
      )}

      <Button
        variant="contained"
        color="primary"
        onClick={handleConvert}
        disabled={!file || !targetFormat || loading}
        fullWidth
      >
        Convert
      </Button>

      <Notification
        open={notification.open}
        message={notification.message}
        severity={notification.severity}
        onClose={closeNotification}
      />
    </Box>
  );
}

export default FileConverter;
